import React from 'react';

const ProblemPage = ({ 
  problemNumber,
  title,
  answer,
  children,
  handleProblemCompletion = () => {} 
}) => {
  const [activeTab, setActiveTab] = React.useState('problem');
  const [userAnswer, setUserAnswer] = React.useState('');
  const [error, setError] = React.useState('');
  const [isSolved, setIsSolved] = React.useState(false);

  const handleSubmit = () => {
    if (userAnswer.toLowerCase() === answer.toLowerCase()) {
      handleProblemCompletion();
      setError('');
      setIsSolved(true);
    } else {
      setError('Incorrect answer. Please try again.');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  return (
    <div className="p-8">
      <div className="max-w-4xl">
        <div className="mb-8">
          <h3 className="text-lg text-gray-400">Problem {problemNumber}</h3>
          <h1 className="text-4xl font-bold mt-2">{title}</h1>
        </div>

        <div className="flex justify-end border-b border-gray-600 mb-6">
          <button 
            className={`px-4 py-2 ${activeTab === 'problem' ? 'border-b-2 border-blue-500 text-blue-500' : 'text-gray-400 hover:text-gray-300'}`}
            onClick={() => setActiveTab('problem')}
          >
            Problem
          </button>
          <button
            className={`px-4 py-2 ${activeTab === 'hint' ? 'border-b-2 border-blue-500 text-blue-500' : 'text-gray-400 hover:text-gray-300'}`}
            onClick={() => setActiveTab('hint')}
          >
            Hint
          </button>
        </div>

        {activeTab === 'problem' ? (
          <div>
            <div className="mb-8">
              {children}
            </div>
            <div className="mt-6">
              <input
                type="text"
                value={userAnswer}
                onChange={(e) => setUserAnswer(e.target.value)}
                onKeyPress={handleKeyPress}
                className="bg-gray-700 text-white px-4 py-2 rounded-lg mr-3 w-64"
                placeholder="Enter your answer"
              />
              <button
                onClick={handleSubmit}
                className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors"
              >
                Submit
              </button>
              {error && <p className="text-red-500 mt-3">{error}</p>}
              {isSolved && (
                <p className="text-green-500 mt-3">Correct! Well done!</p>
              )}
            </div>
          </div>
        ) : (
          <p className="text-gray-300">Here's a hint to help you solve the problem...</p>
        )}
      </div>
    </div>
  );
};

export default ProblemPage;
