// src/components/LandingPage/LandingPage.js

import React from 'react';
import { Link } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = ({ solvedProblems = new Set() }) => {
  return (
    <div className="landing-page-container bg-gray-800 text-white p-8">
      <div className="w-full max-w-4xl mx-auto">
        <h1 className="text-4xl font-bold mb-8">Welcome to the Puzzle Challenge</h1>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <Link 
            to="/problem1"
            className="p-6 bg-gray-700 rounded-lg hover:bg-gray-600 transition-colors"
          >
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-2xl font-bold">Problem 1</h2>
              {solvedProblems.has('problem1') && (
                <span className="text-green-500">✓</span>
              )}
            </div>
            <p className="text-gray-300">-family</p>
          </Link>

          <Link 
            to="/problem2"
            className="p-6 bg-gray-700 rounded-lg hover:bg-gray-600 transition-colors"
          >
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-2xl font-bold">Problem 2</h2>
              {solvedProblems.has('problem2') && (
                <span className="text-green-500">✓</span>
              )}
            </div>
            <p className="text-gray-300">eat with your eyes</p>
          </Link>

          <Link 
            to="/problem3"
            className="p-6 bg-gray-700 rounded-lg hover:bg-gray-600 transition-colors"
          >
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-2xl font-bold">Problem 3</h2>
              {solvedProblems.has('problem3') && (
                <span className="text-green-500">✓</span>
              )}
            </div>
            <p className="text-gray-300">accessible for all?</p>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
