import React from 'react';
import ProblemPage from '../ProblemPage/ProblemPage';

const Problem1 = ({ handleProblemCompletion }) => {
  return (
    <ProblemPage
      problemNumber={1}
      title="-family"
      correctAnswer="hitch"
      handleProblemCompletion={handleProblemCompletion}
      hint={
        <div className="space-y-4">
          <p>Here are some hints to help you solve this puzzle:</p>
          <ul className="list-disc pl-5 space-y-2">
            <li>Hint 1</li>
            <li>Hint 2</li>
            <li className="group relative">
              Answer: <span className="opacity-0 group-hover:opacity-100 transition-opacity bg-red-500">Hitch</span>
            </li>
          </ul>
        </div>
      }
    >
      <div>
        <p>nyc subway signage</p>
        <p>meme</p>
        <p>scientific journal</p>
        <p>film script</p>
        <p>stop sign</p>
      </div>
    </ProblemPage>
  );
};

export default Problem1;
