// src/components/ProblemPage.js

import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProblemPage3.css';

const ProblemPage3 = () => {
  // providing hints
  const [showHint, setShowHint] = useState(false);

  const handleToggleHint = () => {
    setShowHint(!showHint);
  };

  // setting answer
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [incorrectAttempts, setIncorrectAttempts] = useState(0);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the answer is correct (you can replace this with your own logic)
    if (answer.toLowerCase() === '8') {
      setErrorMessage('');
      // Move to the next problem if the answer is correct
      navigate('/endingPage');
    } else {
      // Show an error message if the answer is incorrect
      setErrorMessage('Incorrect answer. Please try again.');
      setIncorrectAttempts((prevAttempts) => prevAttempts + 1);
      // Remove the 'flash' class after a short delay (e.g., 500 milliseconds)
      setTimeout(() => {
        setIncorrectAttempts(0);
      }, 500);


    }
  };

  useEffect(() => {
    console.log('Incorrect Attempts:', incorrectAttempts);

    // Trigger a re-render when incorrectAttempts changes
    // You can toggle a class here or perform other actions
  }, [incorrectAttempts]);


  //image height reference
  const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
  useEffect(() => {
    const image = new Image();
    image.src = "/images/prob-img-3.png";

    image.onload = () => {
      // Once the image is loaded, update the state with its dimensions
      setImageDimensions({ width: image.width, height: image.height });
    };

    // Cleanup: remove the onload handler when the component is unmounted
    return () => {
      image.onload = null;
    };
  }, []);

  //page height
  const [heightClass, setHeightClass] = useState('');
  console.log('Viewport Height:', window.innerHeight);
  console.log('Page Height:', document.body.scrollHeight);
  const pageHeight = document.body.scrollHeight;
  const heightDifference = document.body.scrollHeight - window.innerHeight;

  useEffect(() => {
    // Calculate the difference between scrollHeight and viewport height
    const pageHeight = document.body.scrollHeight;
    const heightDifference = document.body.scrollHeight - window.innerHeight;


    // Set the appropriate class based on the condition
    const newHeightClass = heightDifference > 0 ? `h-12` : `h-2`;

    // Update the state
    setHeightClass(newHeightClass);
  }, []);



  return (
    <div className="md:min-h-screen md:flex md:flex-col xl:h-min">
      {/* Top Row */}
      <div className="flex justify-center items-center h-12 bg-indigo-100 md:bg-indigo-400 lg:bg-indigo-600 xl:bg-indigo-800 2xl:bg-black">
        <div className="flex-none w-12 p-4" />
        <div className="md:flex-none md:w-px h-full md:bg-black" />
        <div className="flex-grow p-4" />
        <div className="md:flex-none md:w-px md:h-full md:bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Header Row */}
      <div className="flex justify-center items-center h-12 bg-white	">
        <div className="flex-none w-12 p-4" />
        <div className="md:flex-none md:w-px md:h-full md:bg-black" />
        <div className="flex-grow p-4 font-mono text-sm	font-semibold uppercase tracking-widest">
          Romeo & Juliet / Problem 03
        </div>
        <div className="md:flex-none md:w-px md:h-full md:bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Problem Row */}
      <div className="flex flex-grow justify-center items-center bg-indigo-100">
        <div className="flex-none w-12 p-4" />
        <div className={`flex-none w-px h-full bg-black ${heightDifference > 0 ? `min-h-calc[${pageHeight}-143px]` : 'min-h-calc[100vh-143px]'}`} />
        <div className="flex-grow p-4 pr-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-2 items-center">
            {/* Problem Statement */}
            <div className="font-sans text-xl md:px-12 ">
