// src/components/ProblemPage.js

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './ProblemPage2.css';

const ProblemPage2 = () => {
    // providing hints
  const [showHint, setShowHint] = useState(false);
  const handleShowHint = () => {
    setShowHint(true);
  };
  const handleCloseHint = () => {
    setShowHint(false);
  };

    // checking the answer
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the answer is correct (you can replace this with your own logic)
    if (answer.toLowerCase() === '6') {
      setErrorMessage('');
      // Move to the next problem if the answer is correct
      navigate('/problem3');
    } else {
      // Show an error message if the answer is incorrect
      setErrorMessage('Incorrect answer. Please try again.');
    }
  };
  return (
    <div className="problem-page-2-container">
      <div className="grid grid-cols-8">
        grid-col-8
      </div>
      <div className="grid grid-cols-6">
        <div className="col-span-3">03</div>
        <div className="col-span-3 text-7xl">07</div>
      </div>
    <div className="problem-page-2">

      <h2 className="uppercase">Problem 2</h2>
      <h3>What is 2x3?</h3>
      <button onClick={handleShowHint}>Hint</button>
      {showHint && (
        <div className="hint-modal">
          <p>Hint: 2+2+2</p>
          <button onClick={handleCloseHint}>Close</button>
        </div>
      )}

      <form onSubmit={handleSubmit}>

        <label>
          Answer:
          <input type="text" value={answer} onChange={handleInputChange} />
        </label>
        <button className="" type="submit">Submit - Go to Problem 3</button>
      </form>
      {errorMessage && <p>{errorMessage}</p>}
    </div>
    </div>

  );
}

export default ProblemPage2;
