import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './ProblemPage2.css';

const ProblemPage2 = () => {
  // providing hints
  const [showHint, setShowHint] = useState(false);
  const handleShowHint = () => {
    setShowHint(true);
  };
  const handleCloseHint = () => {
    setShowHint(false);
  };

  // checking the answer
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the answer is correct (you can replace this with your own logic)
    if (answer.toLowerCase() === '6') {
      setErrorMessage('');
      // Move to the next problem if the answer is correct
      navigate('/problem3');
    } else {
      // Show an error message if the answer is incorrect
      setErrorMessage('Incorrect answer. Please try again.');
    }
  };
  return (
    <div className="problem-page-2-container">
      <div className="grid grid-rows-3 divide-y divide-black justify-items-stretch">
        <div className="flex divide-x divide-black">
          <div className="px-6 max-w-xs">01</div>
          <div className="px-6 grow">02</div>
          <div className="px-6 max-w-xs">03</div>
        </div>
        <div className="flex divide-x divide-black">
          <div className="px-6 max-w-xs">01</div>
          <div className="px-6 grow">Romeo & Juliet / Problem 04</div>
          <div className="px-6 max-w-xs">03</div>
        </div>
        <div className="flex divide-x divide-black">
          <div className="px-6 max-w-xs">01</div>
          <div className="px-6 grow">
            {/* Left side */}
            <div>

          <p>Perfect Romeo! You are closer of enjoying the life with your loved Juliet.
            You have achieved entering in the computer, however it seems like there is a new password.
            Now the password is of 4 numbers. In the screen of your computer appears the following: Huddersfield – Mary Markey</p>
          <form onSubmit={handleSubmit}>

            <label>
              Code:
              <input type="text" value={answer} onChange={handleInputChange} />
            </label>
            <button className="bg-white" type="submit">Submit</button>
          </form>
          {errorMessage && <p>{errorMessage}</p>}
          </div>
          {/* Right side */}
          <div>
          <img src="../public/images/landing-bg.png" width="500" height="600"/>
          </div>

        </div>
          <div className="px-6 max-w-xs">03</div>
        </div>

      </div>

