// src/components/ProblemPage.js

import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProblemPage3.css';

const ProblemPage3 = () => {
  // providing hints
  const [showHint, setShowHint] = useState(false);

  const handleToggleHint = () => {
    setShowHint(!showHint);
  };

  // setting answer
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [incorrectAttempts, setIncorrectAttempts] = useState(0);
  const [firstAttempt, setFirstAttempt] = useState(true);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the answer is correct (you can replace this with your own logic)
    if (answer.toLowerCase() === '8') {
      setErrorMessage('');
      // Move to the next problem if the answer is correct
      navigate('/endingPage');
    } else {
      // Show an error message if the answer is incorrect
      setErrorMessage('Incorrect answer. Please try again.');
      setIncorrectAttempts((prevAttempts) => prevAttempts + 1);

      // Remove the 'flash' class after a short delay (except for the very first attempt)
      if (!firstAttempt) {
        setTimeout(() => {
          setIncorrectAttempts(0);
        }, 500);
      }

      // Update firstAttempt after the first attempt
      setFirstAttempt(false);

    }
  };

  useEffect(() => {
    console.log('Incorrect Attempts:', incorrectAttempts);

    // Trigger a re-render when incorrectAttempts changes
    // You can toggle a class here or perform other actions
  }, [incorrectAttempts]);


  //image height reference
  const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
  useEffect(() => {
    const image = new Image();
    image.src = "/images/prob-img-3.png";

    image.onload = () => {
      // Once the image is loaded, update the state with its dimensions
      setImageDimensions({ width: image.width, height: image.height });
    };

    // Cleanup: remove the onload handler when the component is unmounted
    return () => {
      image.onload = null;
    };
  }, []);




  return (
    <div className="h-screen flex flex-col">
      {/* Top Row */}
      <div className="flex justify-center items-center h-12 bg-indigo-100">
        <div className="flex-none w-12 p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-grow p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Header Row */}
      <div className="flex justify-center items-center h-12 bg-white	">
        <div className="flex-none w-12 p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-grow p-4 font-mono text-sm	font-semibold uppercase tracking-widest">
          Romeo & Juliet / Problem 03
        </div>
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Problem Row */}
      <div className="flex flex-grow justify-center items-center bg-indigo-100">
        <div className="flex-none w-12 p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-grow p-4 pr-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-2 items-center">
            {/* Problem Statement */}
            <div className="font-sans text-xl px-12 ">
              {/* Problem Statement */}
              <div className="pb-6 leading-relaxed ">
                Perfect Romeo! You are closer of enjoying the life with your loved Juliet. You have achieved entering in the computer, however it seems like there is a new password. Now the password is of 4 numbers. In the screen of your computer appears the following: Huddersfield – Mary Markey
              </div>
              {/* Form */}
              <div className="py-8 w-full">
                <form onSubmit={handleSubmit}>
                  <div className="flex items-baseline h-10 w-full">
                    <div className="mr-2 mb-2 font-semibold">
                      Code:
                    </div>
                    <div className="flex-grow w-full pr-2">
                      <input
                        className="h-10 w-full	border border-black mr-2 transition duration-300 focus:outline-none indent-3"
                        type="text"
                        value={answer}
                        onChange={handleInputChange}
                      />
                      <div className={`text-pink-500 text-sm font-semibold pt-2 ${incorrectAttempts > 0 ? 'flash' : ''} `}>
                        {errorMessage && <p>{errorMessage}</p>}
                      </div>

                    </div>
                    <div className="relative bottom-[5.5px]">
                      {/* Button */}
                      <div className="flex-none bg-white text-transparent border border-black rounded-full h-10 px-6 w-[115.5px] font-semibold " >
                        Submit
                      </div>
                      {/* Button shadow */}
                      <button
                        className="relative bottom-[39px] left-[1px] h-[38px] z-10 rounded-full border-b-4 border-solid border-zinc-400	px-6 font-semibold hover:border-zinc-500 active:bg-slate-100"
                        type="submit"
                      >
                        Submit
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </div>
            {/* Supporting Image */}
            <div className="flex flex-col pl-8 py-4">
              <div className="relative">
                {/* Image */}
                <img className="relative w-4/5 z-10 bg-white border rounded-md border-solid border-black p-2"
                  src="/images/prob-img-3.png"
                  alt="problem3" />
                {/* Shadow-like div */}
                <div className={`absolute top-9 left-9 w-4/5 border bg-lime-300 rounded-md border-solid border-black`}
                  style={{ aspectRatio: `${imageDimensions.width} / ${imageDimensions.height}` }}
                />
                <div className="relative pt-12 w-4/5 left-9 text-right"
                  onClick={handleToggleHint}
                >
                  <span className="text-blue-600 cursor-pointer text-sm font-semibold text-right hover:text-blue-700 hover:underline hover:decoration-wavy">
                    {showHint ? 'Hide hint' : 'Need a hint?'}
                  </span>
                </div>
              </div>

              <div className="relative mt-2 bg-white bg-opacity-50 rounded"
                style={{ width: 'calc(80% + 36px)' }}
              >
                {showHint && (
                  <div className="flex flex-row items-stretch text-sm p-2 slide-in-out">
                    <div className="p-2 text-2xl self-center">✨</div>
                    <div className="p-2 self-center">
                      [top number] * [bottom left number] - [bottom right number] = ans
                    </div>
                  </div>
                )}

              </div>
            </div>
          </div>
        </div>
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Footer Row */}
      <div className="flex justify-center items-center bg-indigo-100">
        <div className="flex-none w-12 p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-grow p-3 text-sm  font-mono text-slate-800" >
          © 2023 Escape Room Online Romeo & Juliet
        </div>
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

    </div>
  );

}

export default ProblemPage3;
