import React, { useState, useRef } from 'react';
import { animated, useSpring } from '@react-spring/web';

const HintAccordion = ({ title, children, borderColor }) => {
  const [isOpen, setIsOpen] = useState(false);
  const contentRef = useRef(null);

  const slideAnimation = useSpring({
    height: isOpen ? contentRef.current?.scrollHeight + "px" : "0px",
    opacity: isOpen ? 1 : 0,
    config: { tension: 250, friction: 32 }
  });

  return (
    <div className="border rounded p-2" style={{ borderColor: `${borderColor}80` }}>
      <button 
        className="w-full text-left font-medium focus:outline-none flex items-center"
        onClick={() => setIsOpen(!isOpen)}
      >
        <span className={`transform transition-transform duration-200 ${isOpen ? 'rotate-90' : ''}`}>
          ▸
        </span>
        <span className="ml-2">{title}</span>
      </button>
      <animated.div style={slideAnimation} className="overflow-hidden">
        <div ref={contentRef} className="px-7 py-4">
          {children}
        </div>
      </animated.div>
    </div>
  );
};

export default HintAccordion;
