import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LandingPage from './components/LandingPage/LandingPage';
import Problem1 from './components/Problem1/Problem1';
import Problem2 from './components/Problem2/Problem2';
import Problem3 from './components/Problem3/Problem3';

function App() {
  const [solvedProblems, setSolvedProblems] = React.useState(new Set());

  const handleProblemCompletion = (problemId) => {
    setSolvedProblems(prev => new Set([...prev, problemId]));
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage solvedProblems={solvedProblems} />} />
        <Route 
          path="/problem1" 
          element={<Problem1 handleProblemCompletion={() => handleProblemCompletion('problem1')} />} 
        />
        <Route 
          path="/problem2" 
          element={<Problem2 handleProblemCompletion={() => handleProblemCompletion('problem2')} />} 
        />
        <Route 
          path="/problem3" 
          element={<Problem3 handleProblemCompletion={() => handleProblemCompletion('problem3')} />} 
        />
      </Routes>
    </Router>
  );
}

export default App;
