// src/components/LandingPage/LandingPage.js

import React from 'react';
import Problem1 from '../Problems/Problem1';
import Problem2 from '../Problems/Problem2';
import Problem3 from '../Problems/Problem3';
import HomePage from '../HomePage/HomePage';
import Confetti from 'react-confetti';
import './LandingPage.css';
import { colorThemes, defaultTheme } from '../config/themes';

const LandingPage = ({ handleProblemCompletion = () => {} }) => {
  const [selectedProblem, setSelectedProblem] = React.useState('home');
  const [solvedProblems, setSolvedProblems] = React.useState(new Set());
  const [showConfetti, setShowConfetti] = React.useState(false);
  const [correctAnswers, setCorrectAnswers] = React.useState({
    problem1: '',
    problem2: '',
    problem3: ''
  });
  const [bgColors, setBgColors] = React.useState(defaultTheme);

  const handleThemeChange = (fromColor, toColor, accent, text, active) => {
    setBgColors({ from: fromColor, to: toColor, accent, text, active });
  };

  const handleProblemSelect = (problem) => {
    setSelectedProblem(problem);
  };

  const handleCompletion = (problemId, answer) => {
    handleProblemCompletion();
    const newSolvedProblems = new Set([...solvedProblems, problemId]);
    setSolvedProblems(newSolvedProblems);

    setCorrectAnswers(prev => ({
      ...prev,
      [problemId]: answer
    }));

    if (newSolvedProblems.size === 3) {
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 5000);
    }
  };

  const renderContent = () => {
    switch(selectedProblem) {
      case 'home':
        return <HomePage />;
      case 'problem1':
        return (
          <Problem1 
            handleProblemCompletion={(answer) => handleCompletion('problem1', answer)}
            isSolved={solvedProblems.has('problem1')}
            savedAnswer={correctAnswers.problem1}
            themeColors={bgColors}
          />
        );
      case 'problem2':
        return (
          <Problem2 
            handleProblemCompletion={(answer) => handleCompletion('problem2', answer)}
            isSolved={solvedProblems.has('problem2')}
            savedAnswer={correctAnswers.problem2}
            themeColors={bgColors}
          />
        );
      case 'problem3':
        return (
          <Problem3 
            handleProblemCompletion={(answer) => handleCompletion('problem3', answer)}
            isSolved={solvedProblems.has('problem3')}
            savedAnswer={correctAnswers.problem3}
            themeColors={bgColors}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="landing-page-container bg-gradient-to-b text-[#332E36] h-screen overflow-hidden"
      style={{ 
        backgroundImage: `linear-gradient(to bottom, ${bgColors.from}, ${bgColors.to})` 
      }}
    >
      {showConfetti && <Confetti />}
      <div className="flex h-full">
        <div className="w-64 bg-[#FDFDFD]/30 rounded-xl shadow-xl p-4 m-12 overflow-y-auto">
          <nav className="flex flex-col h-full">
            <div className="space-y-4">
              <button 
                className={`w-full p-2 text-left rounded flex items-center text-[#332E36]`}
                style={{
                  backgroundColor: selectedProblem === 'home' ? bgColors.active : 'transparent',
                  ':hover': { backgroundColor: selectedProblem === 'home' ? bgColors.active : 'rgba(255,255,255,0.2)' }
                }}
                onClick={() => handleProblemSelect('home')}
              >
                🏠 Home
              </button>
              <div className="border-t-2 my-2" style={{ borderColor: bgColors.accent }}></div>
              <button 
                className={`w-full p-2 text-left rounded flex items-center text-[#332E36] ${selectedProblem === 'problem1' ? 'bg-white/40' : 'hover:bg-white/20'}`}
                onClick={() => handleProblemSelect('problem1')}
              >
                {solvedProblems.has('problem1') && <span className="mr-2">✅</span>}
                Problem 1
              </button>
              <button 
                className={`w-full p-2 text-left rounded flex items-center text-[#332E36] ${selectedProblem === 'problem2' ? 'bg-white/40' : 'hover:bg-white/20'}`}
                onClick={() => handleProblemSelect('problem2')}
              >
                {solvedProblems.has('problem2') && <span className="mr-2">✅</span>}
                Problem 2
              </button>
              <button 
                className={`w-full p-2 text-left rounded flex items-center text-[#332E36] ${selectedProblem === 'problem3' ? 'bg-white/40' : 'hover:bg-white/20'}`}
                onClick={() => handleProblemSelect('problem3')}
              >
                {solvedProblems.has('problem3') && <span className="mr-2">✅</span>}
                Problem 3
              </button>
            </div>
            <div className="mt-auto">
              <div className="">
                <div className="flex justify-evenly px-2">
                  {colorThemes.map((theme) => (
                    <button
                      key={theme.name}
                      onClick={() => handleThemeChange(theme.from, theme.to, theme.accent, theme.text, theme.active)}
                      className="rounded-full hover:ring-2 ring-white/50 transition-all border-2 border-white"
                    >
                      <div 
                        className="w-8 h-8 rounded-full" 
                        style={{ 
                          backgroundImage: `linear-gradient(to bottom, ${theme.from}, ${theme.to})` 
                        }}
                      />
                    </button>
                  ))}
                </div>
              </div>
            </div>
          </nav>
        </div>
        <div className="flex-1 p-8 mt-12 mr-12 mb-12 overflow-y-auto">
          {renderContent()}
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
