// src/components/LandingPage/LandingPage.js

import React from 'react';
import { Link } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = ({ handleProblemCompletion = () => {} }) => {
  const [selectedProblem, setSelectedProblem] = React.useState('problem1');
  const [activeTab, setActiveTab] = React.useState('problem');
  const [solvedProblems, setSolvedProblems] = React.useState(new Set());

  const renderContent = () => {
    switch(selectedProblem) {
      case 'problem1': {
        const [answer, setAnswer] = React.useState('');
        const [error, setError] = React.useState('');

        const handleSubmit = () => {
          if (answer.toLowerCase() === 'hitch') {
            handleProblemCompletion();
            setError('');
            setSolvedProblems(prev => new Set([...prev, 'problem1']));
          } else {
            setError('Incorrect answer. Please try again.');
          }
        };

        return (
          <div>
            <h3>Problem 01</h3>
            <h1 className="text-4xl font-bold mb-4">-family</h1>
            <div className="flex justify-end border-b border-gray-600 mb-4">
              <button 
                className={`px-4 py-2 ${activeTab === 'problem' ? 'border-b-2 border-blue-500 text-blue-500' : 'text-gray-400 hover:text-gray-300'}`}
                onClick={() => setActiveTab('problem')}
              >
                Problem
              </button>
              <button
                className={`px-4 py-2 ${activeTab === 'hint' ? 'border-b-2 border-blue-500 text-blue-500' : 'text-gray-400 hover:text-gray-300'}`}
                onClick={() => setActiveTab('hint')}
              >
                Hint
              </button>
            </div>
            {activeTab === 'problem' ? (
              <div>
                <p>nyc subway signage</p>
                <p>meme</p>
                <p>scientific journal</p>
                <p>film script</p>
                <p>stop sign</p>
                <div className="mt-4">
                  <input
                    type="text"
                    value={answer}
                    onChange={(e) => setAnswer(e.target.value)}
                    className="bg-gray-700 text-white px-3 py-2 rounded mr-2"
                    placeholder="Enter your answer"
                  />
                  <button
                    onClick={handleSubmit}
                    className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                  >
                    Submit
                  </button>
                  {error && <p className="text-red-500 mt-2">{error}</p>}
                  {solvedProblems.has('problem1') && (
                    <p className="text-green-500 mt-2">Correct! Well done!</p>
                  )}
                </div>
              </div>
            ) : (
              <p className="text-gray-300">Here's a hint to help you solve the problem...</p>
            )}
          </div>
        );
      }
      case 'problem2':
        return (
          <div>
            <h1 className="text-4xl font-bold mb-4">Problem 2</h1>
            <Link to="/problem2">
              <button className="cta-button" onClick={() => {
                handleProblemCompletion();
                setSolvedProblems(prev => new Set([...prev, 'problem2']));
              }}>Start Problem 2</button>
            </Link>
          </div>
        );
      case 'problem3':
        return (
          <div>
            <h1 className="text-4xl font-bold mb-4">Problem 3</h1>
            <Link to="/problem3">
              <button className="cta-button" onClick={() => {
                handleProblemCompletion();
                setSolvedProblems(prev => new Set([...prev, 'problem3']));
              }}>Start Problem 3</button>
            </Link>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="landing-page-container bg-gray-800 text-white p-8">
      <div className="flex">
        <div className="w-64 bg-gray-900 min-h-screen p-4">
          <nav className="flex flex-col space-y-4">
            <button 
              className={`p-2 text-left rounded flex items-center ${selectedProblem === 'problem1' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => setSelectedProblem('problem1')}
            >
              {solvedProblems.has('problem1') && <span className="mr-2">✓</span>}
              Problem 1
            </button>
            <button 
              className={`p-2 text-left rounded flex items-center ${selectedProblem === 'problem2' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => setSelectedProblem('problem2')}
            >
              {solvedProblems.has('problem2') && <span className="mr-2">✓</span>}
              Problem 2
            </button>
            <button 
              className={`p-2 text-left rounded flex items-center ${selectedProblem === 'problem3' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => setSelectedProblem('problem3')}
            >
              {solvedProblems.has('problem3') && <span className="mr-2">✓</span>}
              Problem 3
            </button>
          </nav>
        </div>
        <div className="flex-1 p-8">
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default LandingPage;
