// src/components/LandingPage/LandingPage.js

import React from 'react';
import './LandingPage.css';

const LandingPage = ({ solvedProblems = new Set() }) => {
  return (
    <div className="p-8">
      <div className="max-w-4xl">
        <h1 className="text-4xl font-bold mb-8">Welcome to the Puzzle Challenge</h1>
        <div className="prose prose-invert">
          <p className="text-xl mb-6">
            Welcome to our puzzle challenge! Select a problem from the navigation menu 
            to begin your journey.
          </p>
          <p className="text-lg mb-4">
            Each puzzle presents a unique challenge that will test your problem-solving abilities.
            Can you solve them all?
          </p>
          <p className="text-lg">
            Your progress will be saved as you complete each puzzle. 
            Good luck!
          </p>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
