// src/components/LandingPage/LandingPage.js

import React from 'react';
import { Link } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = ({ handleProblemCompletion }) => {
  const [selectedProblem, setSelectedProblem] = React.useState('problem1');

  const renderContent = () => {
    switch(selectedProblem) {
      case 'problem1':
        return (
          <div>
            <h3>Problem 01</h3>
            <h1 className="text-4xl font-bold mb-4">-family</h1>
            <div className="flex border-b border-gray-200">
              <button 
                className={`px-4 py-2 text-left ${activeTab === 'problem' ? 'border-b-2 border-blue-500' : ''}`}
                onClick={() => setActiveTab('problem')}
              >
                Problem
              </button>
              <button
                className={`px-4 py-2 text-left ${activeTab === 'hint' ? 'border-b-2 border-blue-500' : ''}`}
                onClick={() => setActiveTab('hint')}
              >
                Hint
              </button>
            </div>
            <div className="mt-4">
              {activeTab === 'problem' ? (
                <div>Problem content goes here</div>
              ) : (
                <div>Hint content goes here</div>
              )}
            </div>
          </div>
        );
      case 'problem2':
        return (
          <div>
            <h1 className="text-4xl font-bold mb-4">Problem 2</h1>
            <Link to="/problem2">
              <button className="cta-button" onClick={handleProblemCompletion}>Start Problem 2</button>
            </Link>
          </div>
        );
      case 'problem3':
        return (
          <div>
            <h1 className="text-4xl font-bold mb-4">Problem 3</h1>
            <Link to="/problem3">
              <button className="cta-button" onClick={handleProblemCompletion}>Start Problem 3</button>
            </Link>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="landing-page-container bg-gray-800 text-white p-8">
      <div className="flex">
        <div className="w-64 bg-gray-900 min-h-screen p-4">
          <nav className="flex flex-col space-y-4">
            <button 
              className={`p-2 text-left rounded ${selectedProblem === 'problem1' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => setSelectedProblem('problem1')}
            >
              Problem 1
            </button>
            <button 
              className={`p-2 text-left rounded ${selectedProblem === 'problem2' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => setSelectedProblem('problem2')}
            >
              Problem 2
            </button>
            <button 
              className={`p-2 text-left rounded ${selectedProblem === 'problem3' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => setSelectedProblem('problem3')}
            >
              Problem 3
            </button>
          </nav>
        </div>
        <div className="flex-1 p-8">
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default LandingPage;
