import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProblemPage.css';

const ProblemPage = () => {
  // providing hints
  const [showHint, setShowHint] = useState(false);

  const handleToggleHint = () => {
    setShowHint(!showHint);
  };

  // setting answer
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [incorrectAttempts, setIncorrectAttempts] = useState(0);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the answer is correct (you can replace this with your own logic)
    if (answer.toLowerCase() === '55') {
      setErrorMessage('');
      // Move to the next problem if the answer is correct
      navigate('/problem2');
    } else {
      // Show an error message if the answer is incorrect
      setErrorMessage('Incorrect answer. Please try again.');
      setIncorrectAttempts((prevAttempts) => prevAttempts + 1);
      // Remove the 'flash' class after a short delay (e.g., 500 milliseconds)
      setTimeout(() => {
        setIncorrectAttempts(0);
      }, 500);


    }
  };




  //image height reference
  const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
  useEffect(() => {
    const image = new Image();
    image.src = "/images/prob-img-1.png";

    image.onload = () => {
      // Once the image is loaded, update the state with its dimensions
      setImageDimensions({ width: image.width, height: image.height });
    };

    // Cleanup: remove the onload handler when the component is unmounted
    return () => {
      image.onload = null;
    };
  }, []);

  const imageAspectRatio = imageDimensions.width / imageDimensions.height;






  // problem height
  const [divSize, setDivSize] = useState({ width: 0, height: 0 });

  useEffect(() => {
    const handleResize = () => {
      // You may replace 'yourDivId' with the actual ID or class of your div element
      const divElement = document.getElementById('problem3Height');

      if (divElement) {
        // Get the current size of the div element
        const width = divElement.offsetWidth;
        const height = divElement.offsetHeight;

        // Update the state with the new size
        setDivSize({ width, height });

        // Log the size to the console
        // console.log(`Div Size - Width: ${width}px, Height: ${height}px`);
      }
    };

    // Attach the event listener
    window.addEventListener('resize', handleResize);

    // Call the handleResize function once to set the initial div height
    handleResize();

    // Detach the event listener on component unmount
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);
  const problemDivHeight = divSize.height * 1.1;







  // Hint width
  const [imageDivSize, setImageDivSize] = useState({ width: 0, height: 0 });

  useEffect(() => {
    const handleResize = () => {
      // You may replace 'yourDivId' with the actual ID or class of your div element
      const divElement = document.getElementById('imageDiv');

      if (divElement) {
        // Get the current size of the div element
        const width = divElement.offsetWidth;
        const height = divElement.offsetHeight;

        // Update the state with the new size
        setImageDivSize({ width, height });

        // Log the size to the console
        console.log(`Image Div Size - Width: ${width}px, Height: ${height}px`);
      }
    };

    // Attach the event listener
    window.addEventListener('resize', handleResize);

    // Call the handleResize function once to set the initial div height
    handleResize();

    // Detach the event listener on component unmount
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const hintWidth = imageDivSize.width + 36;


  return (
    <div className="md:min-h-screen md:flex md:flex-col xl:h-min">
      {/* Top Row */}
      <div className="flex justify-center items-center h-12 bg-orange-100 xs:bg-purple-500 sm:bg-green-500 md:bg-blue-500">
        <div className="flex-none w-12 p-4" />
        <div className="md:flex-none md:w-px h-full md:bg-black" />
        <div className="flex-grow p-4" />
        <div className="md:flex-none md:w-px md:h-full md:bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Header Row */}
      <div className="flex justify-center items-center h-12 bg-white	">
        <div className="flex-none w-12 p-4" />
        <div className="md:flex-none md:w-px md:h-full md:bg-black" />
        <div className="flex-grow p-4 font-mono text-sm	font-semibold uppercase tracking-widest">
          Romeo & Juliet / Problem 01
        </div>
        <div className="md:flex-none md:w-px md:h-full md:bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Problem Row */}
      <div className={`flex flex-grow justify-center items-center bg-orange-100 h-[${problemDivHeight}px]`}>
        <div className="flex-none w-12 p-4" />
        <div className={`flex-none w-px h-full bg-transparent md:bg-black `} />
        <div className={`flex-none w-px h-full bg-transparent md:bg-black `} />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Footer Row */}
      <div className="flex justify-center items-center bg-orange-100 h-10">
        <div className="flex-none w-12 p-4" />
        <div className="flex-none w-px h-full md:bg-black" />
        <div className="flex-grow p-3 text-sm  font-mono text-slate-800" >
          © 2023 Escape Room Online Romeo & Juliet
        </div>
        <div className="flex-none w-px h-full md:bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

    </div>
  );

}

export default ProblemPage;



