// src/components/ProblemPage.js

import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const ProblemPage3 = () => {
  // providing hints
  const [showHint, setShowHint] = useState(false);
  const handleShowHint = () => {
    setShowHint(true);
  };
  const handleCloseHint = () => {
    setShowHint(false);
  };

  if (answer.toLowerCase() === '8') {
    setErrorMessage('');
    navigate('/endingPage');
  } else {
    setErrorMessage('Incorrect answer. Please try again.');
  }


const handleToggleHint = () => {
  setShowHint(!showHint);
};

// setting answer
const [answer, setAnswer] = useState('');
const [errorMessage, setErrorMessage] = useState('');
const navigate = useNavigate();

const handleInputChange = (e) => {
  setAnswer(e.target.value);
};

const handleSubmit = (e) => {
  e.preventDefault();

  // Check if the answer is correct (you can replace this with your own logic)
  if (answer.toLowerCase() === '8') {
    setErrorMessage('');
    // Move to the next problem if the answer is correct
    navigate('/endingPage');
  } else {
    // Show an error message if the answer is incorrect
    setErrorMessage('Incorrect answer. Please try again.');
  }
};

//image height reference
const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
useEffect(() => {
  const image = new Image();
  image.src = "/images/prob-img-3.png";

  image.onload = () => {
    // Once the image is loaded, update the state with its dimensions
    setImageDimensions({ width: image.width, height: image.height });
  };

  // Cleanup: remove the onload handler when the component is unmounted
  return () => {
    image.onload = null;
  };
}, []);





return (
  <div className="h-screen flex flex-col">
    {/* Top Row */}
    <div className="flex justify-center items-center h-12 bg-indigo-100">
      <div className="flex-none w-12 p-4" />
      <div className="flex-none w-px h-full bg-black" />
      <div className="flex-grow p-4" />
      <div className="flex-none w-px h-full bg-black" />
      <div className="flex-none w-12 p-4" />
    </div>

    <div className="h-px bg-black" />

    {/* Header Row */}
    <div className="flex justify-center items-center h-12 bg-white	">
      <div className="flex-none w-12 p-4" />
      <div className="flex-none w-px h-full bg-black" />
      <div className="flex-grow p-4 font-mono text-sm	font-semibold uppercase tracking-widest">
        Romeo & Juliet / Problem 03
      </div>
      <div className="flex-none w-px h-full bg-black" />
      <div className="flex-none w-12 p-4" />
    </div>

    <div className="h-px bg-black" />

    {/* Problem Row */}
    <div className="flex flex-grow justify-center items-center bg-indigo-100">
      <div className="flex-none w-12 p-4" />
      <div className="flex-none w-px h-full bg-black" />
      <div className="flex-grow p-4">
        <div className="grid grid-cols-2 gap-2 items-center">
          {/* Problem Statement */}
          <div className="font-sans text-xl px-12 mr-12">
            <div className="pb-6 leading-relaxed">
              Perfect Romeo! You are closer of enjoying the life with your loved Juliet. You have achieved entering in the computer, however it seems like there is a new password. Now the password is of 4 numbers. In the screen of your computer appears the following: Huddersfield – Mary Markey
            </div>
            <div className="py-8">
              <form onSubmit={handleSubmit}>
                <div className="flex flex-row	items-baseline">
                  <div className="mr-2 mb-2 font-semibold">
                    Code:
                  </div>
                  <div className="flex-shrink">
                    <input className="h-10 border border-black mr-2 transition duration-300 focus:border-2 focus:border-black-500"
                      type="text"
                      value={answer}
                      onChange={handleInputChange}
                    />
                    <div className="text-red-500 text-sm font-semibold pt-2">
                      {errorMessage && <p>{errorMessage}</p>}
                    </div>
                  </div>
                  {/* Button */}
                  <button className="relative bg-white border border-black rounded-full h-10 px-6 font-semibold"
                    type="submit"
                  >
                    Submit
                    {/* Button shadow */}
                    <div className="absolute text-transparent	top-0 left-0 h-[38px] z-10 rounded-full border-b-4 border-solid border-zinc-400	w-[113.5px] px-6 font-semibold">
                      Submit
                    </div>
                  </button>

                </div>
              </form>
            </div>
          </div>
          {/* Supporting Image */}
          <div className="flex flex-col py-4">
            <div className="relative">
              {/* Image */}
              <img className="relative w-4/5 z-10 bg-white border rounded-md border-solid border-black p-2"
                src="/images/prob-img-3.png"
                alt="problem3" />
              {/* Shadow-like div */}
              <div className={`absolute top-9 left-9 w-4/5 border bg-lime-300 rounded-md border-solid border-black`}
                style={{ aspectRatio: `${imageDimensions.width} / ${imageDimensions.height}` }}
              />
            </div>
            <div className="relative pt-16 w-4/5 left-8">
              <div className="text-blue-600 underline text-sm font-semibold text-right"
                onClick={handleToggleHint}
              >
                {showHint ? 'Hide hint' : 'Need a hint?'}
              </div>
              {showHint && (
                <div className="text-lime-300 text-sm font-semibold pt-2">
                  {/* Display your hint content here */}
                  This is a hint. Replace this text with your actual hint.
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <div className="flex-none w-px h-full bg-black" />
      <div className="flex-none w-12 p-4" />
    </div>

    <div className="h-px bg-black" />

    {/* Footer Row */}
    <div className="flex justify-center items-center bg-indigo-100">
      <div className="flex-none w-12 p-4" />
      <div className="flex-none w-px h-full bg-black" />
      <div className="flex-grow p-3 text-sm  font-mono text-slate-800" >
        © 2023 Escape Room Online Romeo & Juliet
      </div>
      <div className="flex-none w-px h-full bg-black" />
      <div className="flex-none w-12 p-4" />
    </div>

  </div>
);

}

export default ProblemPage3;
