// src/components/ProblemPage.js

import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProblemPage3.css';

const ProblemPage3 = () => {
  // providing hints
  const [showHint, setShowHint] = useState(false);
  const handleShowHint = () => {
    setShowHint(true);
  };
  const handleCloseHint = () => {
    setShowHint(false);
  };

  // setting answer
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the answer is correct (you can replace this with your own logic)
    if (answer.toLowerCase() === '8') {
      setErrorMessage('');
      // Move to the next problem if the answer is correct
      navigate('/endingPage');
    } else {
      // Show an error message if the answer is incorrect
      setErrorMessage('Incorrect answer. Please try again.');
    }
  };

  //image height reference
  const [imageHeight, setImageHeight] = useState(0);
  useEffect(() => {
    const image = new Image();
    image.src = "/images/prob-img-3.png";

    image.onload = () => {
      // Once the image is loaded, update the state with its height
      setImageHeight(image.height);
    };

    // Cleanup: remove the onload handler when the component is unmounted
    return () => {
      image.onload = null;
    };
  }, []);




  return (
    <div className="h-screen flex flex-col">
      {/* Top Row */}
      <div className="flex justify-center items-center h-12 bg-indigo-200">
        <div className="flex-none w-12 p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-grow p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Header Row */}
      <div className="flex justify-center items-center h-12 bg-white	">
        <div className="flex-none w-12 p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-grow p-4 font-mono text-sm	font-semibold uppercase tracking-widest">Romeo & Juliet / Problem 03</div>
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Problem Row */}
      <div className="flex flex-grow justify-center items-center bg-indigo-200">
        <div className="flex-none w-12 p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-grow p-4">
          <div className="grid grid-cols-2 gap-2">
            {/* Problem Statement */}
            <div className="font-sans text-xl px-12">
              <div>Perfect Romeo! You are closer of enjoying the life with your loved Juliet. You have achieved entering in the computer, however it seems like there is a new password. Now the password is of 4 numbers. In the screen of your computer appears the following: Huddersfield – Mary Markey</div>
              <div className="py-4">
                <form onSubmit={handleSubmit}>
                  <div className="flex flex-row flex-wrap	items-baseline">
                    <div className="mr-2 mb-2">Code:</div>
                    <input className="h-10 border border-black mr-2 mb-2" type="text" value={answer} onChange={handleInputChange} />
                    <button className="bg-black relative border border-black rounded-full h-10 px-6 font-semibold " type="submit">
                      Submit
                      <div className="button-shadow h-10 px-6 font-semibold">Submit</div>
                    </button>

                  </div>
                </form>
                <div className="text-red-500 text-sm font-semibold pt-2">{errorMessage && <p>{errorMessage}</p>}</div>
              </div>
            </div>
            {/* Supporting Image */}
            <div className="flex flex-col h-full">
              <div className="h-full relative">
                {/* Image */}
                <img className="relative w-4/5 z-10 bg-white border rounded-md border-solid border-black p-2" src="/images/prob-img-3.png" alt="problem3" />
                {/* Shadow-like div */}
                <div className="absolute top-9 left-9 h-[${imageHeight}px] w-4/5 border bg-lime-300 rounded-md border-solid border-black" />
              </div>
              <div className="flex-none text-blue-600 underline text-sm font-semibold pt-12 text-right">Need a hint?</div>
            </div>
          </div>
        </div>
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Footer Row */}
      <div className="flex justify-center items-center bg-indigo-200">
        <div className="flex-none w-12 p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-grow p-4" />
        <div className="flex-none w-px h-full bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>



      {/* <div className="problem-page-3">
      <h2>Problem 3</h2>
      <h3>What is 2x4?</h3>
      <button onClick={handleShowHint}>Hint</button>
      {showHint && (
        <div className="hint-modal">
          <p>Hint: 2+2+2+2</p>
          <button onClick={handleCloseHint}>Close</button>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <label>
          Answer:
          <input type="text" value={answer} onChange={handleInputChange} />
        </label>
        <button className="" type="submit">Submit - Go to Ending Page</button>
      </form>
      {errorMessage && <p>{errorMessage}</p>}
    </div> */}
    </div>
  );

}

export default ProblemPage3;
