// src/components/LandingPage/LandingPage.js

import React from 'react';
import { Link } from 'react-router-dom';
import './LandingPage.css';
import Problem1 from './problems/Problem1';
import Problem2 from './problems/Problem2'; 
import Problem3 from './problems/Problem3';

const LandingPage = ({ handleProblemCompletion = () => {} }) => {
  const [selectedProblem, setSelectedProblem] = React.useState('problem1');
  const [activeTab, setActiveTab] = React.useState('problem');
  const [solvedProblems, setSolvedProblems] = React.useState(new Set());

  const handleProblemSelect = (problem) => {
    setSelectedProblem(problem);
  };

  const handleSolve = (problemId) => {
    handleProblemCompletion();
    setSolvedProblems(prev => new Set([...prev, problemId]));
  };

  const renderContent = () => {
    const commonProps = {
      activeTab,
      setActiveTab,
      isSolved: (id) => solvedProblems.has(id)
    };

    switch(selectedProblem) {
      case 'problem1':
        return <Problem1 {...commonProps} onSolve={() => handleSolve('problem1')} />;
      case 'problem2':
        return <Problem2 {...commonProps} onSolve={() => handleSolve('problem2')} />;
      case 'problem3':
        return <Problem3 {...commonProps} onSolve={() => handleSolve('problem3')} />;
      default:
        return null;
    }
  };

  return (
    <div className="landing-page-container bg-gray-800 text-white p-8">
      <div className="flex">
        <div className="w-64 bg-gray-900 min-h-screen p-4">
          <nav className="flex flex-col space-y-4">
            <button 
              className={`p-2 text-left rounded flex items-center ${selectedProblem === 'problem1' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => handleProblemSelect('problem1')}
            >
              {solvedProblems.has('problem1') && <span className="mr-2">✓</span>}
              Problem 1
            </button>
            <button 
              className={`p-2 text-left rounded flex items-center ${selectedProblem === 'problem2' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => handleProblemSelect('problem2')}
            >
              {solvedProblems.has('problem2') && <span className="mr-2">✓</span>}
              Problem 2
            </button>
            <button 
              className={`p-2 text-left rounded flex items-center ${selectedProblem === 'problem3' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => handleProblemSelect('problem3')}
            >
              {solvedProblems.has('problem3') && <span className="mr-2">✓</span>}
              Problem 3
            </button>
          </nav>
        </div>
        <div className="flex-1 p-8">
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default LandingPage;
