// src/components/ProblemPage.js

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './ProblemPage3.css';

const ProblemPage3 = () => {
  // providing hints
  const [showHint, setShowHint] = useState(false);
  const handleShowHint = () => {
    setShowHint(true);
  };
  const handleCloseHint = () => {
    setShowHint(false);
  };

  // providing hints
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the answer is correct (you can replace this with your own logic)
    if (answer.toLowerCase() === '8') {
      setErrorMessage('');
      // Move to the next problem if the answer is correct
      navigate('/endingPage');
    } else {
      // Show an error message if the answer is incorrect
      setErrorMessage('Incorrect answer. Please try again.');
    }
  };
  return (
    <div className="problem-page-3-container">
    <div className="grid grid-cols-3 gap-4 max-w-screen-lg mx-auto items-center justify-center min-h-screen">
            {/* Left Column */}
            <div className="col-span-1 max-w-[64px]">
        {/* Your content for the left column goes here */}
        <div className="bg-gray-200 p-4">Left</div>
      </div>

      {/* Middle Column */}
      <div className="col-span-1">
        {/* Your content for the middle column goes here */}
        <div className="bg-gray-200 p-4">Middle</div>
      </div>

      {/* Right Column */}
      <div className="col-span-1 max-w-[64px]">
        {/* Your content for the right column goes here */}
        <div className="bg-gray-200 p-4">Right</div>
      </div>

</div>


    <div className="problem-page-3">
      <h2>Problem 3</h2>
      <h3>What is 2x4?</h3>
      <button onClick={handleShowHint}>Hint</button>
      {showHint && (
        <div className="hint-modal">
          <p>Hint: 2+2+2+2</p>
          <button onClick={handleCloseHint}>Close</button>
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <label>
          Answer:
          <input type="text" value={answer} onChange={handleInputChange} />
        </label>
        <button className="" type="submit">Submit - Go to Ending Page</button>
      </form>
      {errorMessage && <p>{errorMessage}</p>}
    </div>
    </div>
  );

}

export default ProblemPage3;
