// src/components/LandingPage/LandingPage.js

import React from 'react';
import Problem1 from '../Problems/Problem1';
import Problem2 from '../Problems/Problem2';
import Problem3 from '../Problems/Problem3';
import HomePage from '../HomePage/HomePage';
import './LandingPage.css';

const LandingPage = ({ handleProblemCompletion = () => {} }) => {
  const [selectedProblem, setSelectedProblem] = React.useState('home');
  const [solvedProblems, setSolvedProblems] = React.useState(new Set());

  const handleProblemSelect = (problem) => {
    setSelectedProblem(problem);
  };

  const handleCompletion = (problemId) => {
    handleProblemCompletion();
    setSolvedProblems(prev => new Set([...prev, problemId]));
  };

  const renderContent = () => {
    switch(selectedProblem) {
      case 'home':
        return <HomePage />;
      case 'problem1':
        return <Problem1 handleProblemCompletion={() => handleCompletion('problem1')} />;
      case 'problem2':
        return <Problem2 handleProblemCompletion={() => handleCompletion('problem2')} />;
      case 'problem3':
        return <Problem3 handleProblemCompletion={() => handleCompletion('problem3')} />;
      default:
        return null;
    }
  };

  return (
    <div className="landing-page-container bg-gradient-to-b from-[#D7BBE3] to-[#F7E9DD] text-white p-8">
      <div className="flex">
        <div className="w-64 bg-white rounded-lg shadow-lg min-h-screen p-4">
          <nav className="flex flex-col space-y-4">
            <button 
              className={`p-2 text-left rounded flex items-center ${selectedProblem === 'home' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => handleProblemSelect('home')}
            >
              üè† Home
            </button>
            <div className="border-t border-gray-700 my-2"></div>
            <button 
              className={`p-2 text-left rounded flex items-center ${selectedProblem === 'problem1' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => handleProblemSelect('problem1')}
            >
              {solvedProblems.has('problem1') && <span className="mr-2">‚úÖ</span>}
              Problem 1
            </button>
            <button 
              className={`p-2 text-left rounded flex items-center ${selectedProblem === 'problem2' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => handleProblemSelect('problem2')}
            >
              {solvedProblems.has('problem2') && <span className="mr-2">‚úÖ</span>}
              Problem 2
            </button>
            <button 
              className={`p-2 text-left rounded flex items-center ${selectedProblem === 'problem3' ? 'bg-gray-700' : 'hover:bg-gray-800'}`}
              onClick={() => handleProblemSelect('problem3')}
            >
              {solvedProblems.has('problem3') && <span className="mr-2">‚úÖ</span>}
              Problem 3
            </button>
          </nav>
        </div>
        <div className="flex-1 p-8">
          {renderContent()}
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
