import React from 'react';

const ProblemPage = ({ 
  problemNumber,
  title,
  correctAnswer,
  children,
  hint,
  handleProblemCompletion = () => {},
  isSolved = false,
  savedAnswer = ''
}) => {
  const [activeTab, setActiveTab] = React.useState('problem');
  const [answer, setAnswer] = React.useState(savedAnswer);
  const [error, setError] = React.useState('');

  React.useEffect(() => {
    if (savedAnswer) {
      setAnswer(savedAnswer);
    }
  }, [savedAnswer]);

  const handleSubmit = () => {
    if (answer.toLowerCase() === correctAnswer.toLowerCase()) {
      handleProblemCompletion(answer);
      setError('');
    } else {
      setError('Incorrect answer. Please try again.');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  return (
    <div>
      <h3 className="font-mono font-thin text-[#C299C2]">Problem {problemNumber.toString().padStart(2, '0')}</h3>
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-4xl text-[#401D40] font-bold">{title}</h1>
        <div className="inline-flex rounded-lg bg-white/30 p-1">
          <button 
            className={`px-4 py-2 rounded-lg ${activeTab === 'problem' ? 'bg-[#BC8ABC] text-[#FAEDFF] font-medium' : 'text-[#C299C2] hover:text-[#9A6D9A] '}`}
            onClick={() => setActiveTab('problem')}
          >
            Problem
          </button>
          <button
            className={`px-4 py-2 rounded-lg ${activeTab === 'hint' ? 'bg-[#BC8ABC] text-white font-medium' : 'text-[#C299C2] hover:text-[#9A6D9A] '}`}
            onClick={() => setActiveTab('hint')}
          >
            Hint
          </button>
        </div>
      </div>
      {activeTab === 'problem' ? (
        <div>
          <br />

          {children}
          <br />
          <div className="mt-4">
            <div className="relative flex">
              <input
                type="text"
                value={answer}
                onChange={(e) => setAnswer(e.target.value)}
                onKeyPress={handleKeyPress}
                className="flex-1 bg-white/50 text-[#332E36] px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-[#D7BBE3]"
                placeholder="Enter your answer"
              />
              <button
                onClick={handleSubmit}
                className="absolute right-0 h-full bg-[#A549CD] text-white px-4 py-2 rounded-r hover:bg-[#9437BC]"
              >
                →
              </button>
            </div>
            {error && <p className="text-red-500 mt-2">{error}</p>}
            {isSolved && (
              <p className="text-green-500 mt-2">Correct! Well done!</p>
            )}
          </div>
        </div>
      ) : (
        <div className="text-[#332E36]">
          {hint || "No hint available for this problem."}
        </div>
      )}
    </div>
  );
};

export default ProblemPage;
