      {/* Problem */}
      <div className="col-span-3 md:col-span-1 p-4">
        {/* Your problem content goes here */}
        <div className="bg-gray-200 p-4">Problem</div>
      </div>

      {/* Hint */}
      <div className="col-span-2 md:col-span-1 p-4">
        {/* Your hint content goes here */}
        <div className="bg-gray-200 p-4">Hint</div>
      </div>

      {/* Submit Button */}
      <div className="col-span-2 md:col-span-1 p-4">
        {/* Your submit button goes here */}
        <button className="bg-blue-500 text-white py-2 px-4 rounded">Submit</button>
      </div>

      {/* Supporting Images */}
      <div className="col-span-2 md:col-span-1 p-4">
        {/* Your supporting images go here */}
        <div className="bg-gray-200 p-4">Image 1</div>
