// src/components/ProblemPage.js

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import AnimatedText from "./AnimatedText";

import './ProblemPage.css'; 



const ProblemPage = () => {
  // providing hints
  const [showHint, setShowHint] = useState(false);
  const handleShowHint = () => {
    setShowHint(true);
  };
  const handleCloseHint = () => {
    setShowHint(false);
  };

  // checking the answer
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the answer is correct (you can replace this with your own logic)
    if (answer.toLowerCase() === '4') {
      setErrorMessage('');
      // Move to the next problem if the answer is correct
      navigate('/problem2');
    } else {
      // Show an error message if the answer is incorrect
      setErrorMessage('Incorrect answer. Please try again.');
    }
  };

  //Text animation stuff
  const [replay, setReplay] = useState(true);
  // Placeholder text data, as if from API
  const placeholderText = [
    { type: "heading1", text: "Framer Motion" },
    {
      type: "heading2",
      text: "Animating responsive text!"
    }
  ];

  const container = {
    visible: {
      transition: {
        staggerChildren: 0.025
      }
    }
  };

  const handleReplay = () => {
    setReplay(!replay);
    setTimeout(() => {
      setReplay(true);
    }, 600);
  };


  return (
    <div className="problem-page-container">

    {/* <motion.div
      initial={{ x: '-100vw' }}
      animate={{ x: 0 }}
      exit={{ x: '100vw' }}
      transition={{ duration: 0.5 }}
  > */}

<motion.div
      className="App"
      initial="hidden"
      // animate="visible"
      animate={replay ? "visible" : "hidden"}
      variants={container}
    >
      <div className="container">
        {placeholderText.map((item, index) => {
          return <AnimatedText {...item} key={index} />;
        })}
      </div>
      <button onClick={handleReplay}>
        Replay <span>‚ü≤</span>
      </button>
    </motion.div>

    <div className="problem-page">


      <h2>Problem 1</h2>

      <h3>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. </h3>
      <button onClick={handleShowHint}>Hint</button>
      {showHint && (
        <div className="hint-modal">
          <p className="text-gray-200	">Hint: 2+2</p>
          <button onClick={handleCloseHint}>Close</button>
        </div>
      )}
      <form onSubmit={handleSubmit}>
        <label>
          Answer:
          <input type="text" value={answer} onChange={handleInputChange} />
        </label>
        <button className="" type="submit">Submit - Go to Problem 2</button>
      </form>
      {errorMessage && <p>{errorMessage}</p>}
    </div>

    {/* </motion.div> */}
    </div>

  );
}

export default ProblemPage;
