import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProblemPage.css';

const ProblemPage = () => {
  // providing hints
  const [showHint, setShowHint] = useState(false);

  const handleToggleHint = () => {
    setShowHint(!showHint);
  };

  // setting answer
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [incorrectAttempts, setIncorrectAttempts] = useState(0);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the answer is correct (you can replace this with your own logic)
    if (answer.toLowerCase() === '55') {
      setErrorMessage('');
      // Move to the next problem if the answer is correct
      navigate('/problem2');
    } else {
      // Show an error message if the answer is incorrect
      setErrorMessage('Incorrect answer. Please try again.');
      setIncorrectAttempts((prevAttempts) => prevAttempts + 1);
      // Remove the 'flash' class after a short delay (e.g., 500 milliseconds)
      setTimeout(() => {
        setIncorrectAttempts(0);
      }, 500);


    }
  };




  //image height reference
  const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
  useEffect(() => {
    const image = new Image();
    image.src = "/images/prob-img-1.png";

    image.onload = () => {
      // Once the image is loaded, update the state with its dimensions
      setImageDimensions({ width: image.width, height: image.height });
    };

    // Cleanup: remove the onload handler when the component is unmounted
    return () => {
      image.onload = null;
    };
  }, []);

  const imageAspectRatio = imageDimensions.width / imageDimensions.height;






  // problem height
  const [divSize, setDivSize] = useState({ width: 0, height: 0 });

  useEffect(() => {
    const handleResize = () => {
      // You may replace 'yourDivId' with the actual ID or class of your div element
      const divElement = document.getElementById('problem3Height');

      if (divElement) {
        // Get the current size of the div element
        const width = divElement.offsetWidth;
        const height = divElement.offsetHeight;

        // Update the state with the new size
        setDivSize({ width, height });

        // Log the size to the console
        // console.log(`Div Size - Width: ${width}px, Height: ${height}px`);
      }
    };

    // Attach the event listener
    window.addEventListener('resize', handleResize);

    // Call the handleResize function once to set the initial div height
    handleResize();

    // Detach the event listener on component unmount
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);
  const problemDivHeight = divSize.height * 1.1;







  // Hint width
  const [imageDivSize, setImageDivSize] = useState({ width: 0, height: 0 });

  useEffect(() => {
    const handleResize = () => {
      // You may replace 'yourDivId' with the actual ID or class of your div element
      const divElement = document.getElementById('imageDiv');

      if (divElement) {
        // Get the current size of the div element
        const width = divElement.offsetWidth;
        const height = divElement.offsetHeight;

        // Update the state with the new size
        setImageDivSize({ width, height });

        // Log the size to the console
        console.log(`Image Div Size - Width: ${width}px, Height: ${height}px`);
      }
    };

    // Attach the event listener
    window.addEventListener('resize', handleResize);

    // Call the handleResize function once to set the initial div height
    handleResize();

    // Detach the event listener on component unmount
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const hintWidth = imageDivSize.width + 36;


  return (
    <div className="md:min-h-screen md:flex md:flex-col xl:h-min">
      {/* Top Row */}
      <div className="flex justify-center items-center h-12 bg-orange-100 xs:bg-purple-500 sm:bg-green-500 md:bg-blue-500">
        <div className="flex-none w-12 p-4" />
        <div className="md:flex-none md:w-px h-full md:bg-black" />
        <div className="flex-grow p-4" />
        <div className="md:flex-none md:w-px md:h-full md:bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Header Row */}
      <div className="flex justify-center items-center h-12 bg-white	">
        <div className="flex-none w-12 p-4" />
        <div className="md:flex-none md:w-px md:h-full md:bg-black" />
        <div className="flex-grow p-4 font-mono text-sm	font-semibold uppercase tracking-widest">
          Romeo & Juliet / Problem 01
        </div>
        <div className="md:flex-none md:w-px md:h-full md:bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Problem Row */}
      <div className={`flex flex-grow justify-center items-center bg-orange-100 h-[${problemDivHeight}px]`}>
        <div className="flex-none w-12 p-4" />
        <div className={`flex-none w-px h-full bg-transparent md:bg-black `} />
        <div id='problem3Height' className="flex-grow p-4 pr-4">
          <div className="grid grid-cols-1 justify-items-center	md:grid-cols-2 gap-2 items-center">
            {/* Problem Statement */}
            <div className="font-sans text-xl lg:w-4/5 md:px-12 ">
              {/* Problem Statement */}
              <div className="pb-8 md:py-8 leading-relaxed ">
              Your descent through the rabbit hole is like a swirling dance of colors and shapes. The world shifts and stretches, and as you land, you find yourselves in a room filled with mysterious doors. Solve the puzzle to uncover the rabbit hole's secrets and choose the right door to continue your adventure.
              </div>
              {/* Form */}
              <div className="py-8 w-full hidden md:block">
                <form onSubmit={handleSubmit}>
                  <div className="flex items-baseline h-10 w-full">
                    <div className="mr-2 mb-2 font-semibold">
                      Code:
                    </div>
                    <div className="flex-grow w-full pr-2">
                      <input
                        className="h-10 w-full	border border-black mr-2 transition duration-300 focus:outline-none indent-3"
                        type="text"
                        value={answer}
                        onChange={handleInputChange}
                      />
                      <div className={`text-pink-500 text-sm font-semibold pt-2 ${incorrectAttempts > 0 ? 'flash' : ''} `}>
                        {errorMessage && <p>{errorMessage}</p>}
                      </div>

                    </div>
                    <div className="relative bottom-[5.5px]">
                      {/* Button */}
                      <div className="flex-none bg-white text-transparent border border-black rounded-full h-10 px-6 w-[115.5px] font-semibold " >
                        Submit
                      </div>
                      {/* Button shadow */}
                      <button
                        className="relative bottom-[39px] left-[1px] h-[38px] z-10 rounded-full border-b-4 border-solid border-zinc-400	px-6 font-semibold hover:border-zinc-500 active:bg-slate-100"
                        type="submit"
                      >
                        Submit
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </div>
            {/* Supporting Image */}
            <div className="flex flex-col md:pl-8 md:py-4">
              <div  className={`relative `}>
                {/* Image */}
                <img id="" className={`relative w-4/5 z-10 bg-white border rounded-md border-solid border-black p-2 md:max-h-[500px] md:max-w-[calc(500px*${imageAspectRatio})]`}
                  src="/images/prob-img-1.png"
                  alt="problem3"
                  style={{ aspectRatio: `${imageDimensions.width} / ${imageDimensions.height}` }}
                />
                {/* Shadow-like div */}
                <div className={`absolute top-9 left-9 w-4/5 border bg-pink-300 rounded-md border-solid border-black md:max-h-[500px] md:max-w-[calc(500px*${imageAspectRatio})]`}
                  style={{ aspectRatio: `${imageDimensions.width} / ${imageDimensions.height}` }}
                />
              </div>
              {/* Hint */}
              <div className={`relative pt-12 w-[${hintWidth}px] xl:right-0  text-right`}
                onClick={handleToggleHint}
              >
                <span className={`text-blue-600 cursor-pointer text-sm font-semibold text-right hover:text-blue-700 hover:underline hover:decoration-wavy `}>
                  {showHint ? 'Hide hint' : 'Need a hint?'}
                </span>
              </div>


              <div className={`relative mt-2 w-[${hintWidth}px] bg-white bg-opacity-50 rounded `}
              >
                {showHint && (
                  <div className="flex flex-row items-stretch text-sm p-2 slide-in-out">
                    <div className="p-2 text-2xl self-center">✨</div>
                    <div className="p-2 self-center">
                      Code = (top number) * (bottom left number) - (bottom right number)
                    </div>
                  </div>
                )}

              </div>
              <div className="md:hidden">
                {/* Form */}
                <div className="py-8 w-full">
                  <form onSubmit={handleSubmit}>
                    <div className="flex items-baseline h-10 w-full">
                      <div className="mr-2 mb-2 font-semibold">
                        Code:
                      </div>
                      <div className="flex-grow w-full pr-2">
                        <input
                          className="h-10 w-full	border border-black mr-2 transition duration-300 focus:outline-none indent-3"
                          type="text"
                          value={answer}
                          onChange={handleInputChange}
                        />
                        <div className={`text-pink-500 text-sm font-semibold pt-2 ${incorrectAttempts > 0 ? 'flash' : ''} `}>
                          {errorMessage && <p>{errorMessage}</p>}
                        </div>

                      </div>
                      <div className="relative bottom-[5.5px]">
                        {/* Button */}
                        <div className="flex-none bg-white text-transparent border border-black rounded-full h-10 px-6 w-[103.91px] font-semibold " >
                          Submit
                        </div>
                        {/* Button shadow */}
                        <button
                          className="relative bottom-[39px] left-[1px] h-[38px] z-10 rounded-full border-b-4 border-solid border-zinc-400	px-6 font-semibold hover:border-zinc-500 active:bg-slate-100"
                          type="submit"
                        >
                          Submit
                        </button>
                      </div>
                    </div>
                  </form>
                </div>

              </div>


            </div>

          </div>
        </div>
        <div className={`flex-none w-px h-full bg-transparent md:bg-black `} />
        <div className="flex-none w-12 p-4" />
      </div>

      <div className="h-px bg-black" />

      {/* Footer Row */}
      <div className="flex justify-center items-center bg-orange-100 h-10">
        <div className="flex-none w-12 p-4" />
        <div className="flex-none w-px h-full md:bg-black" />
        <div className="flex-grow p-3 text-sm  font-mono text-slate-800" >
          © 2023 Escape Room Online Romeo & Juliet
        </div>
        <div className="flex-none w-px h-full md:bg-black" />
        <div className="flex-none w-12 p-4" />
      </div>

    </div>
  );

}

export default ProblemPage;



