import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Layout = ({ children, solvedProblems = new Set() }) => {
  const location = useLocation();

  return (
    <div className="min-h-screen bg-gray-800 text-white">
      <div className="fixed w-64 h-full bg-gray-900 p-6 overflow-y-auto">
        <h2 className="text-xl font-bold mb-6">Puzzle Challenge</h2>
        <nav className="flex flex-col space-y-4">
          <Link 
            to="/"
            className={`p-3 rounded-lg flex items-center transition-colors ${
              location.pathname === '/' ? 'bg-gray-700' : 'hover:bg-gray-700'
            }`}
          >
            <span className="text-lg">Home</span>
          </Link>
          <Link 
            to="/problem1"
            className={`p-3 rounded-lg flex items-center transition-colors ${
              location.pathname === '/problem1' ? 'bg-gray-700' : 'hover:bg-gray-700'
            }`}
          >
            {solvedProblems.has('problem1') && (
              <span className="mr-2 text-green-500">✓</span>
            )}
            <span className="text-lg">Problem 1</span>
          </Link>
          <Link 
            to="/problem2"
            className={`p-3 rounded-lg flex items-center transition-colors ${
              location.pathname === '/problem2' ? 'bg-gray-700' : 'hover:bg-gray-700'
            }`}
          >
            {solvedProblems.has('problem2') && (
              <span className="mr-2 text-green-500">✓</span>
            )}
            <span className="text-lg">Problem 2</span>
          </Link>
          <Link 
            to="/problem3"
            className={`p-3 rounded-lg flex items-center transition-colors ${
              location.pathname === '/problem3' ? 'bg-gray-700' : 'hover:bg-gray-700'
            }`}
          >
            {solvedProblems.has('problem3') && (
              <span className="mr-2 text-green-500">✓</span>
            )}
            <span className="text-lg">Problem 3</span>
          </Link>
        </nav>
      </div>
      <main className="ml-64 min-h-screen">
        {children}
      </main>
    </div>
  );
};

export default Layout;
