// src/components/ProblemPage.js

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './ProblemPage2.css';

const ProblemPage2 = () => {
  // providing hints
  const [showHint, setShowHint] = useState(false);
  const handleShowHint = () => {
    setShowHint(true);
  };
  const handleCloseHint = () => {
    setShowHint(false);
  };

  // checking the answer
  const [answer, setAnswer] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if the answer is correct (you can replace this with your own logic)
    if (answer.toLowerCase() === '6') {
      setErrorMessage('');
      // Move to the next problem if the answer is correct
      navigate('/problem3');
    } else {
      // Show an error message if the answer is incorrect
      setErrorMessage('Incorrect answer. Please try again.');
    }
  };
  return (
    <div className="problem-page-2-container">
      <div className="grid grid-cols-6 gap-4 items-center justify-center min-h-screen">

        {/* Left side */}
        <div className="col-span-3 bg-gray-200 m-4 p-4">
          <h2 className="text-indigo-300 uppercase text-sm font-semibold tracking-widest ">Problem 2</h2>
          <h3 className="text-indigo-800 text-xl py-4">
            You arrive to Julietâ€™s house and in the entrance there is a keypad which asks you for a passcode.
It is a passcode of 3 numbers. What is 2x3?</h3>
          <button onClick={handleShowHint}>Hint</button>
          {showHint && (
            <div className="hint-modal">
              <p>Hint: 2+2+2</p>
              <button onClick={handleCloseHint}>Close</button>
            </div>
          )}

          <form onSubmit={handleSubmit}>

            <label>
              Answer:
              <input type="text" value={answer} onChange={handleInputChange} />
            </label>
            <button className="" type="submit">Submit - Go to Problem 3</button>
          </form>
          {errorMessage && <p>{errorMessage}</p>}
        </div>
        {/* Right side */}
        <div className="col-span-3 bg-gray-400 m-4">03</div>

      </div>
    </div>


  );
}

export default ProblemPage2;
