import React from 'react';

const ProblemPage = ({ 
  problemNumber,
  title,
  correctAnswer,
  children,
  hint,
  handleProblemCompletion = () => {}
}) => {
  const [activeTab, setActiveTab] = React.useState('problem');
  const [answer, setAnswer] = React.useState('');
  const [error, setError] = React.useState('');
  const [isSolved, setIsSolved] = React.useState(false);

  const handleSubmit = () => {
    if (answer.toLowerCase() === correctAnswer.toLowerCase()) {
      handleProblemCompletion();
      setError('');
      setIsSolved(true);
    } else {
      setError('Incorrect answer. Please try again.');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  return (
    <div>
      <h3 className="font-mono font-thin text-[#C299C2]">Problem {problemNumber.toString().padStart(2, '0')}</h3>
      <h1 className="text-4xl text-[#622C62] font-bold mb-4">{title}</h1>
      <div className="flex justify-end mb-4 bg-white/20 rounded-lg p-1">
        <button 
          className={`px-4 py-2 rounded-lg transition-colors ${activeTab === 'problem' ? 'bg-[#A549CD] text-white' : 'text-[#C299C2] hover:bg-white/20'}`}
          onClick={() => setActiveTab('problem')}
        >
          Problem
        </button>
        <button
          className={`px-4 py-2 rounded-lg transition-colors ${activeTab === 'hint' ? 'bg-[#A549CD] text-white' : 'text-[#C299C2] hover:bg-white/20'}`}
          onClick={() => setActiveTab('hint')}
        >
          Hint
        </button>
      </div>
      {activeTab === 'problem' ? (
        <div>
          {children}
          <div className="mt-4">
            <input
              type="text"
              value={answer}
              onChange={(e) => setAnswer(e.target.value)}
              onKeyPress={handleKeyPress}
              className="bg-white/50 text-[#332E36] px-3 py-2 rounded mr-2 focus:outline-none focus:ring-2 focus:ring-[#D7BBE3]"
              placeholder="Enter your answer"
            />
            <button
              onClick={handleSubmit}
              className="bg-[#A549CD] text-white px-4 py-2 rounded hover:bg-[#9437BC]"
            >
              Submit
            </button>
            {error && <p className="text-red-500 mt-2">{error}</p>}
            {isSolved && (
              <p className="text-green-500 mt-2">Correct! Well done!</p>
            )}
          </div>
        </div>
      ) : (
        <div className="text-gray-300">
          {hint || "No hint available for this problem."}
        </div>
      )}
    </div>
  );
};

export default ProblemPage;
