import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Layout = ({ children, solvedProblems = new Set() }) => {
  const location = useLocation();

  return (
    <div className="landing-page-container bg-gray-800 text-white p-8">
      <div className="flex">
        <div className="w-64 bg-gray-900 min-h-screen p-4 fixed left-0 top-0">
          <nav className="flex flex-col space-y-4">
            <Link 
              to="/"
              className={`p-2 text-left rounded flex items-center ${
                location.pathname === '/' ? 'bg-gray-700' : 'hover:bg-gray-800'
              }`}
            >
              Home
            </Link>
            <Link 
              to="/problem1"
              className={`p-2 text-left rounded flex items-center ${
                location.pathname === '/problem1' ? 'bg-gray-700' : 'hover:bg-gray-800'
              }`}
            >
              {solvedProblems.has('problem1') && <span className="mr-2">✓</span>}
              Problem 1
            </Link>
            <Link 
              to="/problem2"
              className={`p-2 text-left rounded flex items-center ${
                location.pathname === '/problem2' ? 'bg-gray-700' : 'hover:bg-gray-800'
              }`}
            >
              {solvedProblems.has('problem2') && <span className="mr-2">✓</span>}
              Problem 2
            </Link>
            <Link 
              to="/problem3"
              className={`p-2 text-left rounded flex items-center ${
                location.pathname === '/problem3' ? 'bg-gray-700' : 'hover:bg-gray-800'
              }`}
            >
              {solvedProblems.has('problem3') && <span className="mr-2">✓</span>}
              Problem 3
            </Link>
          </nav>
        </div>
        <div className="ml-64 flex-1">
          {children}
        </div>
      </div>
    </div>
  );
};

export default Layout;
