// src/components/LandingPage/LandingPage.js

import React from 'react';
import { Link, Outlet } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = ({ handleProblemCompletion = () => {} }) => {
  const [solvedProblems, setSolvedProblems] = React.useState(new Set());

  const problems = [
    {
      id: 'problem1',
      title: 'Problem 1',
      path: '/problem1'
    },
    {
      id: 'problem2', 
      title: 'Problem 2',
      path: '/problem2'
    },
    {
      id: 'problem3',
      title: 'Problem 3', 
      path: '/problem3'
    }
  ];

  return (
    <div className="landing-page-container bg-gray-800 text-white p-8">
      <div className="flex">
        {/* Sidebar navigation - now persistent */}
        <div className="w-64 bg-gray-900 min-h-screen p-4">
          <nav className="flex flex-col space-y-4">
            <Link
              to="/"
              className="p-2 text-left rounded flex items-center hover:bg-gray-800 border-b border-gray-700 pb-4 mb-2"
            >
              <span className="mr-2">üè†</span>
              Welcome
            </Link>
            {problems.map(problem => (
              <Link 
                key={problem.id}
                to={problem.path}
                className={`p-2 text-left rounded flex items-center hover:bg-gray-800`}
              >
                {solvedProblems.has(problem.id) && <span className="mr-2">‚úì</span>}
                {problem.title}
              </Link>
            ))}
          </nav>
        </div>
        {/* Main content area - will be replaced by Outlet */}
        <div className="flex-1 p-8">
          <Outlet />
          {/* Default welcome content shown only on root route */}
          {window.location.pathname === '/' && (
            <>
              <h1 className="text-4xl font-bold mb-4">Welcome to the Problem Set</h1>
              <p className="text-gray-300 mb-4">Select a problem from the sidebar to begin.</p>
              <p className="text-gray-300">
                Problems completed: {solvedProblems.size} / {problems.length}
              </p>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default LandingPage;
